const moment = require('moment')
const rowOffset = 6

function nowStr () {
    return moment().format('YYYY-MM-DD')
}

function generateEmptyCell (repeat, className) {
    const cells = []

    for (let i = 0; i < repeat; i++) {
        cells.push(`<td class="empty-cell ${className || ''}"></td>`)
    }

    return new Handlebars.SafeString(cells.join(''))
}

function oddClassName (index) {
    return (index + 1) % 2 !== 0 ? 'odd' : ''
}

function sum (a, b) {
    return a + b
}

function getDetailRowIndex (index) {
    return (index + 1) + rowOffset
}

function nowPlus20Days() {
    var date = new Date()
    date.setDate(date.getDate() + 20);
    return date.toLocaleDateString();
}

function total(items) {
    var sum = 0
    items.forEach(function (i) {
        console.log('Calculating item ' + i.name + '; you should see this message in debug run')
        sum += i.price
    })
    return sum
}

function getPageNumber (pageIndex) {
    if (pageIndex == null) {
        return ''
    }

    const pageNumber = pageIndex + 1

    return pageNumber
}

function getTotalPages (pages) {
    if (!pages) {
        return ''
    }

    return pages.length
}

